SELECT agent_id
FROM sys_user
WHERE id = #{userId}

SELECT sr.id, sr.username, sr.nickname, sr.campus_id agent_id, sr.email, IFNULL((SELECT am.name FROM agent_management am WHERE am.id = sr.campus_id), (SELECT am.name FROM agent_management am WHERE am.id = sr.agent_id)) AS `name`, se.role_name, sr.mobile, sr.status, IFNULL((SELECT am.name FROM agent_management am WHERE am.id = sr.agent_id), se.role_name) AS roleFather, sr.logintime FROM sys_user sr LEFT JOIN agent_management AS am ON am.id = sr.campus_id INNER JOIN sys_user_role sur ON sur.user_id = sr.id INNER JOIN sys_role se ON sur.role_id = se.id WHERE (sr.agent_id = (SELECT agent_id FROM sys_user WHERE id = #{userId}) OR (SELECT agent_id FROM sys_user WHERE id = #{userId}) = '*' OR (SELECT agent_id FROM sys_user WHERE id = #{userId}) = '**')
and sr.username = #{username}
and se.role_name = #{roleName}
and `name` = #{name}
and sr.email = #{email}
and sr.mobile = #{mobile}
and sr.status = #{status}
and sr.status = 1
GROUP BY sr.id

select id, username,nickname, email, mobile,avatar
from sys_user
where id = #{id}

