mapper.insert:
INSERT INTO carousel (image_url, title, description, link_url, order_number)
VALUES (#{carousel.imageUrl}, #{carousel.title}, #{carousel.description}, #{carousel.linkUrl},
        #{carousel.orderNumber});

mapper.update:
UPDATE advert
status=0 status=1
WHERE id=#{id}

UPDATE carousel
is_active=0 is_active=1
WHERE id=#{id}

mapper.select:
SELECT id, advert_name, content, img, sort
FROM advert
WHERE id = #{id}
