mapper.update:
UPDATE type_name
SET sort=#{sort}
WHERE id = #{id}

UPDATE class_type
status=0 status=1
WHERE id=#{id}

mapper.select:
SELECT type_age
FROM class_type
WHERE class_ftype_id = #{id}
  AND type_age = #{age}
  AND type_name = #{name}

SELECT sort
FROM class_type
WHERE type_name = #{name}
  AND class_ftype_id = #{id}

SELECT id
FROM type_name
WHERE `name` = #{name}
  AND type_id = #{id}

SELECT MAX(sort)
FROM type_name
WHERE type_id = #{typeId}
